import React, { Fragment, useEffect, useState } from "react";

//import EditTodo from "./EditTodo";

const ListSR = () => {
  const [serviceRequests, setServiceRequests] = useState([]);

  //delete todo function
const deleteSR = async id => {
    try {
        let deleteSR = {
            id: id
          }
        const requestOptions = {
            method: 'DELETE',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(deleteSR)
        };
        await fetch(`http:/localhost:3001/delete_service_request`, requestOptions)
          .then(response => response.json())
          .then(response => {
          if(response.status === "failed")
          alert(response.message)})


        setServiceRequests(serviceRequests.filter(each => each.id !== id));
    } catch (err) {
      console.error(err.message);
    }
  };

  const getSR = async () => {
    try {
      const response = await fetch("http://localhost:3001/service_requests");
      const jsonData = await response.json();

      setServiceRequests(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getSR();
  }, []);

  console.log(serviceRequests);

  return (
    <Fragment>
      {" "}
      <table class="table mt-5 text-center">
        <thead>
          <tr>
            <th>Name</th>
            <th>Contractor</th>
            <th>Task</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {/*<tr>
            <td>John</td>
            <td>Doe</td>
            <td>john@example.com</td>
          </tr> */}
          {serviceRequests.map(each => (
            <tr key={each.id}>
              <td>{each.sr_name}</td>
              <td>{each.sr_contractor}</td>
              <td>{each.sr_task}</td>
              <td>
                {/* <EditSR each={each} /> */}
              </td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => deleteSR(each.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Fragment>
  );
};

export default ListSR;





